import { Button, Card, Popover, Avatar } from 'antd';
import { useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import { HeartOutlined, RetweetOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons';
import PostImages from '../components/PostImages';

const PostCard = ({ post }) => {
    
    const { me } = useSelector((state) => state.user);
    const id = me && me.id;
    // const id = me?.id; 위와 같음 . 새로운 문법 => 옵셔널 체이닝
    //  위의 두줄을 이렇게도 가능 (옵셔널 체이닝을 통해서)
    // const id = useSelector((state) => state.user.me?.id);

    const [liked, setLiked] = useState(false);

    return (
        <div style={{ marginBottom: '20px' }}>
            <Card
                cover={post.Images[0] && <PostImages images={post.Images}/>}
                actions={[
                    <RetweetOutlined key="retweet"/>,
                    <HeartOutlined key="heart"/>,
                    <MessageOutlined key="comment"/>,
                    <Popover key="more" content={(
                        <Button.Group>
                            {id && post.User.id === id ? (
                                <>
                                    <Button>수정</Button>
                                    <Button type='danger'>삭제</Button>
                                </>
                            ) : (<Button>신고</Button>)}
                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </Popover>
                ]}
            >
                <Card.Meta 
                    avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                    title={post.User.nickname}
                    description={post.content}
                />
            </Card>
        </div>
    )
};

PostCard.propTypes = {
    post: PropTypes.shape({
        id: PropTypes.number,
        User: PropTypes.object,
        content: PropTypes.string,
        createdAt: PropTypes.object,
        Comments: PropTypes.arrayOf(PropTypes.object), // 객체들의 배열이라는 의미
        Images: PropTypes.arrayOf(PropTypes.object) // 객체들의 배열이라는 의미
    }).isRequired
}

export default PostCard;
