import { useState, useCallback } from 'react';
import { Button, Card, Popover, Avatar, List, Comment } from 'antd';
import { useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import { HeartTwoTone, HeartOutlined, RetweetOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons';
import PostImages from '../components/PostImages';
import CommentForm from '../components/CommentForm';
import PostCardContent from '../components/PostCardContent';
import { useDispatch } from 'react-redux';

const PostCard = ({ post }) => {
    const dispatch = useDispatch();
    const { me } = useSelector((state) => state.user);
    const id = me && me.id;
    // const id = me?.id; 위와 같음 . 새로운 문법 => 옵셔널 체이닝
    //  위의 두줄을 이렇게도 가능 (옵셔널 체이닝을 통해서)
    // const id = useSelector((state) => state.user.me?.id);

    const [liked, setLiked] = useState(false);
    const [commentFormOpened, setCommentFormOpened] = useState(false);
    const onToggleLike = useCallback(() => {
        setLiked((prev) => !prev);
    }, []);
    const onToggleComment = useCallback(() => {
        setCommentFormOpened((prev) => !prev);
    }, []);

    const onRemovePost = useCallback(() => {
        dispatch({
            type: REMOVE_POST_REQUEST
        })
    }, []);

    return (
        <div style={{ marginBottom: '20px' }}>
            <Card
                cover={post.Images[0] && <PostImages images={post.Images}/>}
                actions={[
                    <RetweetOutlined key="retweet"/>,
                    liked 
                        ? <HeartTwoTone twoToneColor="#eb2f96" key="heart" onClick={onToggleLike}/>
                        : <HeartOutlined towToneColor="#eb2f96" key="heart" onClick={onToggleLike}/>,
                    <MessageOutlined key="comment" onClick={onToggleComment}/>,
                    <Popover key="more" content={(
                        <Button.Group>
                            {id && post.User.id === id ? (
                                <>
                                    <Button>수정</Button>
                                    <Button type='danger' onClick={onRemovePost}>삭제</Button>
                                </>
                            ) : (<Button>신고</Button>)}
                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </Popover>
                ]}
            >
                <Card.Meta 
                    avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                    title={post.User.nickname}
                    description={<PostCardContent postData={post.content}/>}
                />
            </Card>
            {commentFormOpened && (
                <div>
                    <CommentForm post={post}/>
                    <List 
                        header={`${post.Comments.length}개의 댓글`}
                        itemLayout="horizontal"
                        dataSource={post.Comments}
                        renderItem={(item) => (
                            <li>
                                <Comment 
                                    author={item.User.nickname}
                                    avatar={<Avatar>{item.User.nickname[0]}</Avatar>}
                                    content={item.content}
                                />
                            </li>
                        )}
                    />
                </div>
            )}
        </div>
    )
};

PostCard.propTypes = {
    post: PropTypes.shape({
        id: PropTypes.number,
        User: PropTypes.object,
        content: PropTypes.string,
        createdAt: PropTypes.object,
        Comments: PropTypes.arrayOf(PropTypes.object), // 객체들의 배열이라는 의미
        Images: PropTypes.arrayOf(PropTypes.object) // 객체들의 배열이라는 의미
    }).isRequired
}

export default PostCard;
